const Item = ({ key, note, date, time, deleteData }) => {
    function deleteItem() {
        deleteData(function (prev) {
            return (prev.filter(item => item.key !== key))
        })
    }
    return (
        <div className="item" >
            <div>
                <p>{note}</p>
                <p>{`${date} ${time}`}</p>
            </div>
            <button className="remove" onClick={deleteItem()}>delete</button>
        </div>
    );
}

export default Item
